class Illustration extends StatelessWidget {
  final PathInfo info;
  final double? height;
  final Color? tint;

  const Illustration({
    required this.info,
    this.height,
    this.tint,
  });

  const Illustration.posp({
    this.height,
    this.tint,
  }) : info = IllustrationPaths.posp;

  const Illustration.leaflet({
    this.height,
  })  : info = IllustrationPaths.leaflet,
        tint = null;

  Illustration.archive({
    IllustrationPalette palette = IllustrationPalette.light,
    this.height,
  })  : info = IllustrationPaths(palette: palette).archive,
        tint = null;

  Illustration.trash({
    IllustrationPalette palette = IllustrationPalette.light,
    this.height,
  })  : info = IllustrationPaths(palette: palette).trash,
        tint = null;

  Illustration.noNotes({
    IllustrationPalette palette = IllustrationPalette.light,
    this.height,
  })  : info = IllustrationPaths(palette: palette).noNotes,
        tint = null;

  Illustration.nothingFound({
    IllustrationPalette palette = IllustrationPalette.light,
    this.height,
  })  : info = IllustrationPaths(palette: palette).nothingFound,
        tint = null;

  Illustration.typeToSearch({
    IllustrationPalette palette = IllustrationPalette.light,
    this.height,
  })  : info = IllustrationPaths(palette: palette).typeToSearch,
        tint = null;

  Illustration.backupRestore({
    IllustrationPalette palette = IllustrationPalette.light,
    this.height,
  })  : info = IllustrationPaths(palette: palette).backupRestore,
        tint = null;

  @override
  Widget build(BuildContext context) {
    final double _height = height ?? info.size.height;
    final PathInfo _info = info.copyWith(tint: tint);

    return CustomPaint(
      size: Size(
        _height * _info.size.aspectRatio,
        _height,
      ),
      isComplex: true,
      painter: PathInfoPainter(
        info: _info,
        height: _height,
      ),
    );
  }
}

